import React, { Component } from "react";
import "./SoundboardSquare.css";

class SoundboardSquare extends Component {
  constructor(props) {
		super(props);

		this.state= {
			soundURL: '',
		};

    this.handleUploadSound= this.handleUploadSound.bind(this);
    this.playSound= this.playSound.bind(this);
	}

	handleUploadSound(e) {
		e.preventDefault();

		const data= new FormData();
		data.append('file', this.uploadInput.files[0]);
		data.append('filename', this.fileName.value);

		fetch('http://localhost:8000/upload', {
			method: 'POST',
			body: data,
		}).then((response) => {
			response.json().then((body) => {
				this.setState({ soundURL: `http://localhost:8000/${body.file}` });
			});
		});
	}

  playSound() {
    var audio= new Audio(this.state.soundURL);
    audio.play();
  }

  render() {
    const uploadButton = document.querySelector('.browse-btn');
    const fileInfo = document.querySelector('.file-info');
    const realInput = document.getElementById('fileSelect');

    return (
      <div className="squareMain">
          <form onSubmit= {this.handleUploadSound}>
            <div className="container">
              <input id="fileSelect" type="file"/>
              <button class="browse-btn" >Browse Files</button>
              <span class="file-info">Upload a file</span>
            </div>
            <div>
					    <input id="nameSelect" ref={(ref) => { this.fileName = ref; }} type="text" placeholder="Sound name"/>
				    </div>
            <div className="buttons">
              <button id="upload">Upload</button>
              <button id="play" onClick= {this.playSound}>Play</button>
            </div>
          </form>
      </div>
    );
  }
};

export default SoundboardSquare;
