import React, { Component } from "react"; 
//import "./Slider.css";

class Slider extends Component {
  constructor(props) {
    super(props);
    this.state= {
      volume: this.props.value,
    };
  }

  render() {
    return(

    <div className="sliderContainer">
        <input type="range" onChange={this.props.onChange} min="1" max="100" value={this.state.volume} className="sliderMain"></input>
    </div>
    );
  }

}

export default Slider;
